//https://www.hackerrank.com/challenges/compare-the-triplets/problem#include <bits/stdc++.h>using namespace std;vector<int> compareTriplets(vector<int> a, vector<int> b){    vector<int> score{0,0};    for(int i = 0; i < a.size() && i < b.size(); i++)    {        if(a[i] > b[i])        {            score[0] = score[0] + 1;        }        else if(a[i] < b[i])        {            score[1] = score[1] + 1;        }        else        {            continue;        }    }    return score;}int main(){    vector<int> a{5,6,7};    vector<int> b{3,6,10};    vector<int> result = compareTriplets(a, b);		for (int i = 0; i < result.size(); i++) {        cout << result[i];        if (i != result.size() - 1) {            cout << " ";        }    }    cout << "\n";    return 0;}